    stx $d020
    stx $d021

copy_data_loop:
    .for (var i = 0; i < 4; i++){
        lda screenData1 +256*i, x
        sta 1024 + 256*i, x

        lda screenColorData1 +256*i, x
        sta $d800 + 256*i, x
    }
    inx
    bne copy_data_loop

WAIT_KEY:
	jsr KernalGETIN
	beq WAIT_KEY

copy_data_loop2:
    .for (var i = 0; i < 4; i++){
        lda screenData2 +256*i, x
        sta 1024 + 256*i, x

        lda screenColorData2 +256*i, x
        sta $d800 + 256*i, x
    }
    inx
    bne copy_data_loop2

WAIT_KEY2:
	jsr KernalGETIN
	beq WAIT_KEY2
    rts
.print "End main code $" + toHexString(*) + " [" + * + "]"
.print "Size: " + (* - start)

